mapper.select:
SELECT 

FROM planfundsearch a

WHERE a.id = #{id}

SELECT
FROM contractplanfund a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.create_by = #{createBy.id}
and cm.fundnatureid=#{fundnatureid}
AND cm.contractname like '%'||#{contractname}||'%' CONCAT('%', #{contractname}, '%')
AND cm.contractno like '%'||#{contractno}||'%' CONCAT('%', #{contractno}, '%')
and cm.contractclassid=#{contractclassid}
AND ct.customername like '%'||#{customername}||'%' CONCAT('%', #{customername}, '%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM planfundsearch a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.create_by = #{createBy.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE planfundsearch SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
