mapper.select:
SELECT 

FROM flowagent a

WHERE a.id = #{id}

SELECT 

FROM flowagent a


	a.del_flag = #{DEL_FLAG_NORMAL} and a.create_by = #{currentUser.id}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM flowagent a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE flowagent SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
